const MyBookings = () => {
  const [activeFilter, setActiveFilter] = useState('all');

  // Mock user bookings - in real app, this would come from API
  const userBookings = bookings;

  const getStatusIcon = status => {
    switch (status) {
      case 'confirmed':
        return <FaCheckCircle />;
      case 'pending':
        return <FaExclamationTriangle />;
      case 'cancelled':
        return <FaTimesCircle />;
      default:
        return <FaClock />;
    }
  };

  const filteredBookings = userBookings.filter(booking => {
    if (activeFilter === 'all') return true;
    return booking.status === activeFilter;
  });

  const formatDate = dateString => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
    });
  };

  const calculateNights = (checkIn, checkOut) => {
    const start = new Date(checkIn);
    const end = new Date(checkOut);
    const diffTime = Math.abs(end - start);
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  const handleContactHotel = booking => {
    alert(
      `Contact information for ${booking.hotelName}:\nPhone: +91 1800-HOTELS\nEmail: info@${booking.hotelName.toLowerCase().replace(/\s+/g, '')}.com`
    );
  };

  const handleViewDetails = booking => {
    alert(
      `Booking Details:\nBooking ID: ${booking.id}\nGuest: ${booking.guestName}\nEmail: ${booking.guestEmail}\nPhone: ${booking.guestPhone}\nSpecial Requests: ${booking.specialRequests || 'None'}`
    );
  };

  if (filteredBookings.length === 0) {
    return (
      <PageContainer>
        <HotelNavbar />

        <Container>
          <PageHeader>
            <PageTitle>My Bookings</PageTitle>
            <PageSubtitle>Manage your hotel reservations</PageSubtitle>
          </PageHeader>

          <FilterTabs>
            <FilterTab
              active={activeFilter === 'all'}
              onClick={() => setActiveFilter('all')}
            >
              All Bookings
            </FilterTab>
            <FilterTab
              active={activeFilter === 'confirmed'}
              onClick={() => setActiveFilter('confirmed')}
            >
              Confirmed
            </FilterTab>
            <FilterTab
              active={activeFilter === 'pending'}
              onClick={() => setActiveFilter('pending')}
            >
              Pending
            </FilterTab>
            <FilterTab
              active={activeFilter === 'cancelled'}
              onClick={() => setActiveFilter('cancelled')}
            >
              Cancelled
            </FilterTab>
          </FilterTabs>

          <EmptyState>
            <FaCalendarAlt className="icon" />
            <h3>No bookings found</h3>
            <p>
              {activeFilter === 'all'
                ? "You haven't made any hotel bookings yet. Start exploring amazing hotels!"
                : `No ${activeFilter} bookings found. Try a different filter.`}
            </p>
            {activeFilter === 'all' && (
              <BrowseButton to="/hotels">Browse Hotels</BrowseButton>
            )}
          </EmptyState>
        </Container>

        <HotelFooter />
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <HotelNavbar />

      <Container>
        <PageHeader>
          <PageTitle>My Bookings</PageTitle>
          <PageSubtitle>Manage your hotel reservations</PageSubtitle>
        </PageHeader>

        <FilterTabs>
          <FilterTab
            active={activeFilter === 'all'}
            onClick={() => setActiveFilter('all')}
          >
            All Bookings ({userBookings.length})
          </FilterTab>
          <FilterTab
            active={activeFilter === 'confirmed'}
            onClick={() => setActiveFilter('confirmed')}
          >
            Confirmed (
            {userBookings.filter(b => b.status === 'confirmed').length})
          </FilterTab>
          <FilterTab
            active={activeFilter === 'pending'}
            onClick={() => setActiveFilter('pending')}
          >
            Pending ({userBookings.filter(b => b.status === 'pending').length})
          </FilterTab>
          <FilterTab
            active={activeFilter === 'cancelled'}
            onClick={() => setActiveFilter('cancelled')}
          >
            Cancelled (
            {userBookings.filter(b => b.status === 'cancelled').length})
          </FilterTab>
        </FilterTabs>

        <BookingsGrid>
          {filteredBookings.map(booking => (
            <BookingCard key={booking.id} status={booking.status}>
              <BookingHeader>
                <BookingInfo>
                  <HotelName>{booking.hotelName}</HotelName>
                  <RoomName>{booking.roomName}</RoomName>
                </BookingInfo>
                <BookingActions>
                  <StatusBadge status={booking.status}>
                    {getStatusIcon(booking.status)}
                    {booking.status}
                  </StatusBadge>
                </BookingActions>
              </BookingHeader>

              <BookingDetails>
                <DetailItem>
                  <FaCalendarAlt className="icon" />
                  <span>
                    <span className="label">Check-in:</span>
                    {formatDate(booking.checkIn)}
                  </span>
                </DetailItem>

                <DetailItem>
                  <FaCalendarAlt className="icon" />
                  <span>
                    <span className="label">Check-out:</span>
                    {formatDate(booking.checkOut)}
                  </span>
                </DetailItem>

                <DetailItem>
                  <FaUsers className="icon" />
                  <span>
                    <span className="label">Guests:</span>
                    {booking.guests}
                  </span>
                </DetailItem>

                <DetailItem>
                  <FaClock className="icon" />
                  <span>
                    <span className="label">Nights:</span>
                    {calculateNights(booking.checkIn, booking.checkOut)}
                  </span>
                </DetailItem>
              </BookingDetails>

              <BookingFooter>
                <TotalPrice>
                  Total: {booking.totalPrice.toLocaleString()}
                </TotalPrice>

                <div style={{ display: 'flex', gap: theme.spacing.md }}>
                  <ActionButton onClick={() => handleViewDetails(booking)}>
                    <FaEye />
                    View Details
                  </ActionButton>

                  <ActionButton onClick={() => handleContactHotel(booking)}>
                    <FaPhone />
                    Contact Hotel
                  </ActionButton>
                </div>
              </BookingFooter>
            </BookingCard>
          ))}
        </BookingsGrid>
      </Container>

      <HotelFooter />
    </PageContainer>
  );
};

