const RoomDetail = () => {
  const { slug, roomId } = useParams();
  const navigate = useNavigate();
  const [hotel, setHotel] = useState(null);
  const [room, setRoom] = useState(null);
  const [selectedImage, setSelectedImage] = useState(0);
  const [bookingData, setBookingData] = useState({
    checkIn: '',
    checkOut: '',
    guests: '2',
  });

  useEffect(() => {
    const foundHotel = getHotelByIdOrSlug(slug);

    const foundRoom = foundHotel ? getRoomById(foundHotel.id, roomId) : null;
    setHotel(foundHotel);
    setRoom(foundRoom);
  }, [slug, roomId]);

  const getAmenityIcon = amenity => {
    const iconMap = {
      WiFi: FaWifi,
      TV: FaTv,
      AC: FaSnowflake,
      'Mini Bar': FaCoffee,
      'Room Service': FaCoffee,
    };
    const IconComponent = iconMap[amenity] || FaCheckCircle;
    return <IconComponent />;
  };

  const handleInputChange = e => {
    setBookingData({
      ...bookingData,
      [e.target.name]: e.target.value,
    });
  };

  const calculateNights = () => {
    if (bookingData.checkIn && bookingData.checkOut) {
      const checkIn = new Date(bookingData.checkIn);
      const checkOut = new Date(bookingData.checkOut);
      const diffTime = Math.abs(checkOut - checkIn);
      return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    }
    return 1;
  };

  const calculateTotal = () => {
    const nights = calculateNights();
    return room ? room.price * nights : 0;
  };

  const handleBooking = e => {
    e.preventDefault();
    navigate(`/${slug}/rooms/booking/${roomId}`, {
      state: { bookingData },
    });
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: `${room?.name} at ${hotel?.name}`,
        text: room?.description,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      alert('Room link copied to clipboard!');
    }
  };

  if (!hotel || !room) {
    return (
      <PageContainer>
        <HotelNavbar />
        <Container>
          <div style={{ textAlign: 'center', padding: '4rem 0' }}>
            <h2>Room not found</h2>
            <p>The room you're looking for doesn't exist.</p>
            <Link to="/hotels" style={{ color: theme.colors.primary }}>
              Back to Hotels
            </Link>
          </div>
        </Container>
        <HotelFooter />
      </PageContainer>
    );
  }

  const images = room.images || [room.image];

  return (
    <PageContainer>
      <HotelNavbar />
      <Container>
        <BackButton onClick={() => navigate(-1)}>
          <FaArrowLeft />
          Back to Hotel
        </BackButton>

        <RoomGrid>
          <RoomContent>
            <ImageGallery>
              <MainImage src={images[selectedImage]} alt={room.name} />
              {images.length > 1 && (
                <ThumbnailGrid>
                  {images.map((image, index) => (
                    <Thumbnail
                      key={index}
                      src={image}
                      alt={`${room.name} ${index + 1}`}
                      active={selectedImage === index}
                      onClick={() => setSelectedImage(index)}
                    />
                  ))}
                </ThumbnailGrid>
              )}
            </ImageGallery>

            <RoomHeader>
              <RoomInfo>
                <RoomName>{room.name}</RoomName>
                <RoomType>{room.type}</RoomType>
                <RoomDetails>
                  <Detail>
                    <FaBed />
                    {room.bedType}
                  </Detail>
                  <Detail>
                    <FaUsers />
                    Up to {room.maxGuests} guests
                  </Detail>
                </RoomDetails>
                <Price>
                  {room.price.toLocaleString()}{' '}
                  <span
                    style={{ fontSize: '1rem', color: theme.colors.gray600 }}
                  >
                    per night
                  </span>
                </Price>
              </RoomInfo>

              <RoomActions>
                <ActionButton onClick={handleShare} title="Share Room">
                  <FaShareAlt />
                </ActionButton>
                <ActionButton title="Add to Wishlist">
                  <FaHeart />
                </ActionButton>
              </RoomActions>
            </RoomHeader>

            <Description>{room.description}</Description>

            <Section>
              <SectionTitle>Room Amenities</SectionTitle>
              <AmenitiesGrid>
                {room.amenities?.map((amenity, index) => (
                  <AmenityItem key={index}>
                    <AmenityIcon>{getAmenityIcon(amenity)}</AmenityIcon>
                    <span>{amenity}</span>
                  </AmenityItem>
                ))}
              </AmenitiesGrid>
            </Section>

            <Section>
              <SectionTitle>Room Policies & Details</SectionTitle>
              <PoliciesGrid>
                <PolicyCard>
                  <h4>
                    <FaCheckCircle style={{ color: theme.colors.success }} />
                    Guest Policies
                  </h4>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">
                      Couple Friendly - Unmarried couples welcome
                    </span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">Local ID accepted for check-in</span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">
                      Pet friendly (additional charges apply)
                    </span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">24/7 front desk assistance</span>
                  </PolicyItem>
                </PolicyCard>

                <PolicyCard>
                  <h4>
                    <FaCoffee style={{ color: theme.colors.primary }} />
                    Services & Facilities
                  </h4>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">
                      Complimentary breakfast included
                    </span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">Free high-speed WiFi</span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">24/7 room service available</span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">Daily housekeeping service</span>
                  </PolicyItem>
                </PolicyCard>

                <PolicyCard>
                  <h4>
                    <FaUsers style={{ color: theme.colors.primary }} />
                    Check-in Policies
                  </h4>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">Check-in: 2:00 PM onwards</span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">Check-out: 12:00 PM</span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">
                      Early check-in subject to availability
                    </span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">Valid ID required at check-in</span>
                  </PolicyItem>
                </PolicyCard>

                <PolicyCard>
                  <h4>
                    <FaSnowflake style={{ color: theme.colors.primary }} />
                    Cancellation Policy
                  </h4>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">
                      Free cancellation up to 24 hours before check-in
                    </span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">
                      No-show charges: 100% of first night
                    </span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">
                      Flexible booking options available
                    </span>
                  </PolicyItem>
                  <PolicyItem>
                    <FaCheckCircle className="icon" />
                    <span className="text">Refundable security deposit</span>
                  </PolicyItem>
                </PolicyCard>
              </PoliciesGrid>
            </Section>
          </RoomContent>

          <BookingCard>
            <h3
              style={{
                marginBottom: theme.spacing.lg,
                fontSize: '1.3rem',
                fontWeight: '600',
              }}
            >
              Book This Room
            </h3>

            <BookingForm onSubmit={handleBooking}>
              <FormGroup>
                <Label htmlFor="checkIn">Check-in Date</Label>
                <Input
                  type="date"
                  id="checkIn"
                  name="checkIn"
                  value={bookingData.checkIn}
                  onChange={handleInputChange}
                  min={new Date().toISOString().split('T')[0]}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="checkOut">Check-out Date</Label>
                <Input
                  type="date"
                  id="checkOut"
                  name="checkOut"
                  value={bookingData.checkOut}
                  onChange={handleInputChange}
                  min={
                    bookingData.checkIn ||
                    new Date().toISOString().split('T')[0]
                  }
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="guests">Number of Guests</Label>
                <Select
                  id="guests"
                  name="guests"
                  value={bookingData.guests}
                  onChange={handleInputChange}
                >
                  {Array.from({ length: room.maxGuests }, (_, i) => (
                    <option key={i + 1} value={i + 1}>
                      {i + 1} Guest{i > 0 ? 's' : ''}
                    </option>
                  ))}
                </Select>
              </FormGroup>

              {bookingData.checkIn && bookingData.checkOut && (
                <PriceSummary>
                  <SummaryRow>
                    <span>
                      {room.price.toLocaleString()}  {calculateNights()} night
                      {calculateNights() > 1 ? 's' : ''}
                    </span>
                    <span>{calculateTotal().toLocaleString()}</span>
                  </SummaryRow>
                  <SummaryRow className="total">
                    <span>Total</span>
                    <span>{calculateTotal().toLocaleString()}</span>
                  </SummaryRow>
                </PriceSummary>
              )}

              <BookButton type="submit">Book Now</BookButton>
            </BookingForm>

            <p
              style={{
                fontSize: '0.8rem',
                color: theme.colors.gray500,
                textAlign: 'center',
                marginTop: theme.spacing.md,
              }}
            >
              You won't be charged yet
            </p>
          </BookingCard>
        </RoomGrid>
      </Container>

      <HotelFooter />
    </PageContainer>
  );
};

export default RoomDetail;

