const RoomList = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const [hotel, setHotel] = useState(null);
  const [loading, setLoading] = useState(true);

  // Get updated hotel data from Redux if available
  const { hotels } = useSelector(state => state.hotelManagement);
  const [searchParams, setSearchParams] = useState({
    checkIn: '',
    checkOut: '',
    guests: '2',
  });
  const [sortBy, setSortBy] = useState('price-low');
  const [filterBy, setFilterBy] = useState('all');

  useEffect(() => {
    // First check if we have updated hotel data in Redux state
    const updatedHotel = hotels.find(h => h.slug === slug);

    if (updatedHotel) {
      // Use updated hotel data from Redux (includes admin changes)
      setHotel(updatedHotel);
    } else {
      // Fallback to original dummy data
      const foundHotel = getHotelByIdOrSlug(slug);
      setHotel(foundHotel);
    }

    setLoading(false);
  }, [slug, hotels]);

  const handleSearchChange = e => {
    const { name, value } = e.target;
    setSearchParams(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const getSortedRooms = () => {
    if (!hotel?.rooms) return [];

    let rooms = [...hotel.rooms];

    // Filter rooms
    if (filterBy !== 'all') {
      rooms = rooms.filter(
        room => room.type.toLowerCase() === filterBy.toLowerCase()
      );
    }

    // Sort rooms
    switch (sortBy) {
      case 'price-low':
        return rooms.sort((a, b) => a.price - b.price);
      case 'price-high':
        return rooms.sort((a, b) => b.price - a.price);
      case 'guests':
        return rooms.sort((a, b) => b.maxGuests - a.maxGuests);
      case 'name':
        return rooms.sort((a, b) => a.name.localeCompare(b.name));
      default:
        return rooms;
    }
  };

  const handleSearch = () => {
    // Here you could implement availability checking
  };

  const clearFilters = () => {
    setFilterBy('all');
    setSortBy('price-low');
  };

  if (loading) {
    return (
      <PageContainer>
        <HotelNavbar />
        <Container>
          <div style={{ textAlign: 'center', padding: '4rem 0' }}>
            <h2>Loading...</h2>
          </div>
        </Container>
        <HotelFooter />
      </PageContainer>
    );
  }

  if (!hotel) {
    return (
      <PageContainer>
        <HotelNavbar />
        <Container>
          <div style={{ textAlign: 'center', padding: '4rem 0' }}>
            <h2>Hotel not found</h2>
            <p>The hotel you're looking for doesn't exist.</p>
          </div>
        </Container>
        <HotelFooter />
      </PageContainer>
    );
  }

  const sortedRooms = getSortedRooms();
  const uniqueRoomTypes = [
    ...new Set(hotel.rooms?.map(room => room.type) || []),
  ];
  console.log(hotel);

  return (
    <PageContainer>
      <HotelNavbar />

      <Container>
        <BackButton onClick={() => navigate(`/${hotel.slug}`)}>
          <FaArrowLeft />
          Back to Hotel
        </BackButton>

        <PageHeader>
          <HotelInfo>
            <HotelDetails>
              <HotelName>{hotel.name}</HotelName>
              <HotelLocation>
                <FaMapMarkerAlt />
                {hotel.location}
              </HotelLocation>
              <HotelRating>
                <FaStar style={{ color: '#fbbf24' }} />
                {hotel.rating}/5 ({hotel.starRating} Star Hotel)
              </HotelRating>
            </HotelDetails>
            <HotelImage image={hotel.image} />
          </HotelInfo>
        </PageHeader>

        <BookingControls>
          <ControlsGrid>
            <FormGroup>
              <Label htmlFor="checkIn">Check-in Date</Label>
              <Input
                type="date"
                id="checkIn"
                name="checkIn"
                value={searchParams.checkIn}
                onChange={handleSearchChange}
                min={new Date().toISOString().split('T')[0]}
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="checkOut">Check-out Date</Label>
              <Input
                type="date"
                id="checkOut"
                name="checkOut"
                value={searchParams.checkOut}
                onChange={handleSearchChange}
                min={
                  searchParams.checkIn || new Date().toISOString().split('T')[0]
                }
              />
            </FormGroup>

            <FormGroup>
              <Label htmlFor="guests">Guests</Label>
              <Select
                id="guests"
                name="guests"
                value={searchParams.guests}
                onChange={handleSearchChange}
              >
                {[1, 2, 3, 4, 5, 6, 7, 8].map(num => (
                  <option key={num} value={num}>
                    {num} Guest{num > 1 ? 's' : ''}
                  </option>
                ))}
              </Select>
            </FormGroup>

            <FilterButton
              className={filterBy !== 'all' ? 'active' : ''}
              onClick={() =>
                setFilterBy(
                  filterBy === 'all'
                    ? uniqueRoomTypes[0]?.toLowerCase() || 'all'
                    : 'all'
                )
              }
            >
              <FaFilter />
              Filter
            </FilterButton>

            <SearchButton onClick={handleSearch}>
              <FaSearch />
              Search
            </SearchButton>
          </ControlsGrid>
        </BookingControls>

        <ResultsHeader>
          <ResultsCount>
            <span className="count">{sortedRooms.length}</span> Room
            {sortedRooms.length !== 1 ? 's' : ''} Available
          </ResultsCount>

          <SortControls>
            <SortLabel>Sort by:</SortLabel>
            <Select value={sortBy} onChange={e => setSortBy(e.target.value)}>
              <option value="price-low">Price: Low to High</option>
              <option value="price-high">Price: High to Low</option>
              <option value="guests">Max Guests</option>
              <option value="name">Room Name</option>
            </Select>

            {uniqueRoomTypes.length > 1 && (
              <>
                <SortLabel>Filter:</SortLabel>
                <Select
                  value={filterBy}
                  onChange={e => setFilterBy(e.target.value)}
                >
                  <option value="all">All Room Types</option>
                  {uniqueRoomTypes.map(type => (
                    <option key={type} value={type.toLowerCase()}>
                      {type} Rooms
                    </option>
                  ))}
                </Select>
              </>
            )}
          </SortControls>
        </ResultsHeader>

        {sortedRooms.length > 0 ? (
          <RoomsGrid>
            {sortedRooms.map(room => (
              <RoomCard
                key={room.id}
                room={room}
                hotelId={hotel.id}
                hotelSlug={hotel.slug}
              />
            ))}
          </RoomsGrid>
        ) : (
          <NoRoomsMessage>
            <h3>No rooms found</h3>
            <p>
              No rooms match your current search criteria. Try adjusting your
              filters or dates.
            </p>
            <ClearFiltersButton onClick={clearFilters}>
              Clear Filters
            </ClearFiltersButton>
          </NoRoomsMessage>
        )}
      </Container>

      <HotelFooter />
    </PageContainer>
  );
};

export default RoomList;

import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  businesses: [],
  editingBusiness: null,
  originalBusiness: null,
  changes: {},
  hasUnsavedChanges: false,
  activeBusinessId: null,
  sectionVisibility: {
    hero: true,
    about: true,
    services: true,
    portfolio: true,
    skills: true,
    experience: true,
    team: true,
    gallery: true,
    contact: true,
    packages: true,
    testimonials: true,
  },
};

const businessManagementSlice = createSlice({
  name: 'businessManagement',
  initialState,
  reducers: {
    setEditingBusiness: (state, action) => {
      const businessId = action.payload;
      let business = state.businesses.find(b => b.id === businessId);

      // If business is not found in Redux state, we'll initialize with null
      // and let the component handle fetching from the dummy data
      if (!business) {
        console.warn(`Business with id ${businessId} not found in Redux state`);
        state.editingBusiness = null;
        state.originalBusiness = null;
      } else {
        state.editingBusiness = { ...business };
        state.originalBusiness = { ...business };
      }

      state.activeBusinessId = businessId;
      state.changes = {};
      state.hasUnsavedChanges = false;
    },

    updateBusinessField: (state, action) => {
      const { field, value } = action.payload;
      if (state.editingBusiness) {
        state.editingBusiness[field] = value;
        state.changes[field] = {
          old: state.originalBusiness[field],
          new: value,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessImage: (state, action) => {
      const { field, url } = action.payload;
      if (state.editingBusiness) {
        state.editingBusiness[field] = url;
        state.changes[field] = {
          old: state.originalBusiness[field],
          new: url,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessGallery: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.gallery = action.payload;
        state.changes.gallery = {
          old: state.originalBusiness.gallery,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessServices: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.services = action.payload;
        state.changes.services = {
          old: state.originalBusiness.services,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessTestimonials: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.testimonials = action.payload;
        state.changes.testimonials = {
          old: state.originalBusiness.testimonials,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessPackages: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.packages = action.payload;
        state.changes.packages = {
          old: state.originalBusiness.packages,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessPortfolio: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.portfolio = action.payload;
        state.changes.portfolio = {
          old: state.originalBusiness.portfolio,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessSkills: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.skills = action.payload;
        state.changes.skills = {
          old: state.originalBusiness.skills,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessExperience: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.experience = action.payload;
        state.changes.experience = {
          old: state.originalBusiness.experience,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessTeam: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.team = action.payload;
        state.changes.team = {
          old: state.originalBusiness.team,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateBusinessCustomSections: (state, action) => {
      if (state.editingBusiness) {
        state.editingBusiness.customSections = action.payload;
        state.changes.customSections = {
          old: state.originalBusiness.customSections || [],
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
    },

    saveBusinessChanges: state => {
      if (state.editingBusiness && state.hasUnsavedChanges) {
        const businessIndex = state.businesses.findIndex(
          b => b.id === state.editingBusiness.id
        );
        if (businessIndex !== -1) {
          state.businesses[businessIndex] = { ...state.editingBusiness };
        } else {
          // Add new business if it doesn't exist
          state.businesses.push({ ...state.editingBusiness });
        }
        state.originalBusiness = { ...state.editingBusiness };
        state.changes = {};
        state.hasUnsavedChanges = false;
      }
    },

    discardBusinessChanges: state => {
      if (state.originalBusiness) {
        state.editingBusiness = { ...state.originalBusiness };
        state.changes = {};
        state.hasUnsavedChanges = false;
      }
    },

    initializeBusiness: (state, action) => {
      const business = action.payload;
      const existingBusiness = state.businesses.find(b => b.id === business.id);

      if (!existingBusiness) {
        state.businesses.push(business);
      }
    },

    clearEditingBusiness: state => {
      state.editingBusiness = null;
      state.originalBusiness = null;
      state.changes = {};
      state.hasUnsavedChanges = false;
      state.activeBusinessId = null;
    },

    toggleBusinessSectionVisibility: (state, action) => {
      const { section } = action.payload;
      state.sectionVisibility[section] = !state.sectionVisibility[section];

      // Also update the editing business's sectionVisibility
      if (state.editingBusiness) {
        if (!state.editingBusiness.sectionVisibility) {
          state.editingBusiness.sectionVisibility = {
            ...state.sectionVisibility,
          };
        }
        state.editingBusiness.sectionVisibility[section] =
          state.sectionVisibility[section];
        state.changes.sectionVisibility = {
          old: state.originalBusiness?.sectionVisibility || {},
          new: state.editingBusiness.sectionVisibility,
        };
      }

      state.hasUnsavedChanges = true;
    },
  },
});

export const {
  setEditingBusiness,
  initializeBusiness,
  updateBusinessField,
  updateBusinessImage,
  updateBusinessGallery,
  updateBusinessServices,
  updateBusinessTestimonials,
  updateBusinessPackages,
  updateBusinessPortfolio,
  updateBusinessSkills,
  updateBusinessExperience,
  updateBusinessTeam,
  updateBusinessCustomSections,
  saveBusinessChanges,
  discardBusinessChanges,
  clearEditingBusiness,
  toggleBusinessSectionVisibility,
} = businessManagementSlice.actions;

export default businessManagementSlice.reducer;

import { createSlice } from '@reduxjs/toolkit';
import { hotels } from '../../DummyData/hotels';

const initialState = {
  hotels: hotels,
  editingHotel: null,
  originalHotel: null,
  changes: {},
  hasUnsavedChanges: false,
  activeHotelId: null,
  sectionVisibility: {
    hero: true,
    about: true,
    features: true,
    gallery: true,
    amenities: true,
    contact: true,
  },
};

const hotelManagementSlice = createSlice({
  name: 'hotelManagement',
  initialState,
  reducers: {
    setEditingHotel: (state, action) => {
      const hotel = state.hotels.find(h => h.id === action.payload);
      state.editingHotel = hotel ? { ...hotel } : null;
      state.originalHotel = hotel ? { ...hotel } : null;
      state.activeHotelId = action.payload;
      state.changes = {};
      state.hasUnsavedChanges = false;
    },

    updateHotelField: (state, action) => {
      const { field, value } = action.payload;
      if (state.editingHotel) {
        state.editingHotel[field] = value;
        state.changes[field] = {
          old: state.originalHotel[field],
          new: value
        };
        state.hasUnsavedChanges = true;
      }
    },

    updateHotelImage: (state, action) => {
      const { index, url } = action.payload;
      if (state.editingHotel) {
        if (index === 'main') {
          state.editingHotel.image = url;
          state.changes.image = {
            old: state.originalHotel.image,
            new: url
          };
        } else {
          const newImages = [...state.editingHotel.images];
          newImages[index] = url;
          state.editingHotel.images = newImages;
          state.changes.images = {
            old: state.originalHotel.images,
            new: newImages
          };
        }
        state.hasUnsavedChanges = true;
      }
    },

    addHotelImage: (state, action) => {
      if (state.editingHotel) {
        const newImages = [...state.editingHotel.images, action.payload];
        state.editingHotel.images = newImages;
        state.changes.images = {
