    updatePackages: (state, action) => {
      if (state.editingVendor) {
        state.editingVendor.packages = action.payload;
        state.changes.packages = {
          old: state.originalVendor.packages,
          new: action.payload,
        };
        state.hasUnsavedChanges = true;
      }
