const BookingPage = () => {
  const { vendorSlug } = useParams();
  const navigate = useNavigate();
  const [vendor, setVendor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [submitted] = useState(false);
  const [errors, setErrors] = useState({});

  const [formData, setFormData] = useState({
    // Contact Information
    firstName: '',
    lastName: '',
    email: '',
    phone: '',

    // Event Details
    eventDate: '',
    eventTime: '',
    guestCount: '',
    eventType: 'wedding',
    venue: '',
    budget: '',

    // Services
    services: [],

    // Additional Information
    message: '',
    contactPreference: 'email',
    timeline: '',
  });

  const serviceOptions = [
    'Wedding Planning',
    'Venue Decoration',
    'Floral Arrangements',
    'Photography',
    'Videography',
    'Catering',
    'Music & DJ',
    'Transportation',
    'Cake & Desserts',
    'Hair & Makeup',
  ];

  useEffect(() => {
    const vendorData = getVendorById(vendorSlug);
    setVendor(vendorData);
    setLoading(false);
  }, [vendorSlug]);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: '',
      }));
    }
  };

  const handleCheckboxChange = service => {
    setFormData(prev => ({
      ...prev,
      services: prev.services.includes(service)
        ? prev.services.filter(s => s !== service)
        : [...prev.services, service],
    }));
  };

  const validateForm = () => {
    const newErrors = {};

    // Required fields
    const requiredFields = [
      'firstName',
      'lastName',
      'email',
      'phone',
      'eventDate',
      'guestCount',
    ];

    requiredFields.forEach(field => {
      if (!formData[field]) {
        newErrors[field] = 'This field is required';
      }
    });

    // Email validation
    if (formData.email && !/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    // Phone validation
    if (formData.phone && !/^\+?[\d\s\-\(\)]{10,}$/.test(formData.phone)) {
      newErrors.phone = 'Please enter a valid phone number';
    }

    // Date validation
    if (formData.eventDate) {
      const eventDate = new Date(formData.eventDate);
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      if (eventDate < today) {
        newErrors.eventDate = 'Event date must be in the future';
      }
    }

    // Guest count validation
    if (
      formData.guestCount &&
      (formData.guestCount < 1 || formData.guestCount > 1000)
    ) {
      newErrors.guestCount = 'Guest count must be between 1 and 1000';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async e => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setSubmitting(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Generate booking ID and redirect to confirmation
      const bookingId = 'WED-' + Date.now().toString().slice(-6);
      const confirmationParams = new URLSearchParams({
        booking: bookingId,
        vendor: vendor.id,
        name: `${formData.firstName} ${formData.lastName}`,
        email: formData.email,
        phone: formData.phone,
        date: formData.eventDate,
        time: formData.eventTime,
        guests: formData.guestCount,
        type: formData.eventType,
        services: formData.services.join(','),
        message: formData.message,
      });

      navigate(
        `/weddings/booking-confirmation?${confirmationParams.toString()}`
      );
    } catch (error) {
      alert('Failed to submit booking request. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <PageContainer>
        <LoadingState>Loading booking form...</LoadingState>
      </PageContainer>
    );
  }

  if (!vendor) {
    return (
      <PageContainer>
        <NavHeader>
          <NavContent>
            <BackButton onClick={() => navigate('/weddings')}>
              <FaArrowLeft />
              Back to Vendors
            </BackButton>
          </NavContent>
        </NavHeader>
        <Container>
          <div style={{ textAlign: 'center', padding: '4rem' }}>
            <h2>Vendor not found</h2>
            <p>The vendor you're trying to book doesn't exist.</p>
          </div>
        </Container>
      </PageContainer>
    );
  }

  if (submitted) {
    return (
      <PageContainer>
        <NavHeader>
          <NavContent>
            <BackButton onClick={() => navigate(`/${vendor.id}`)}>
              <FaArrowLeft />
              Back to Vendor
            </BackButton>
            <PageTitle>
              <FaRing />
              Booking Request Submitted
            </PageTitle>
