      phone: '+1 (555) 345-6789',
      message: 'Traditional wedding with elegant floral arrangements',
    },
  ];

  useEffect(() => {
    setBookings(mockBookings);
    setFilteredBookings(mockBookings);
  }, []);

  useEffect(() => {
    let filtered = [...bookings];

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(
        booking =>
          booking.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
          booking.contactName
            .toLowerCase()
            .includes(searchTerm.toLowerCase()) ||
          booking.eventType.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Filter by status
    if (statusFilter !== 'all') {
      filtered = filtered.filter(booking => booking.status === statusFilter);
    }

    // Sort bookings
    switch (sortBy) {
      case 'date':
        filtered.sort((a, b) => new Date(a.date) - new Date(b.date));
        break;
      case 'created':
        filtered.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
        break;
      case 'status':
        filtered.sort((a, b) => a.status.localeCompare(b.status));
        break;
      default:
        break;
    }

    setFilteredBookings(filtered);
  }, [bookings, searchTerm, statusFilter, sortBy]);

  const getVendorDetails = vendorId => {
    return weddingVendors.find(vendor => vendor.id === vendorId);
  };

  const getStatusIcon = status => {
    switch (status) {
      case 'confirmed':
        return <FaCheckCircle />;
      case 'pending':
        return <FaExclamationCircle />;
      case 'cancelled':
        return <FaTimesCircle />;
      default:
        return <FaClock />;
    }
  };

  const handleViewBooking = booking => {
    const vendor = getVendorDetails(booking.vendorId);
    if (vendor) {
      navigate(
        `/weddings/booking-confirmation?booking=${booking.id}&vendor=${booking.vendorId}&name=${booking.contactName}&email=${booking.email}&phone=${booking.phone}&date=${booking.date}&time=${booking.time}&guests=${booking.guestCount}&type=${booking.eventType}&services=${booking.services.join(',')}&message=${booking.message}`
      );
    }
  };

  const handleViewVendor = vendorId => {
    navigate(`/${vendorId}`);
  };

  const stats = {
    total: bookings.length,
    confirmed: bookings.filter(b => b.status === 'confirmed').length,
    pending: bookings.filter(b => b.status === 'pending').length,
    cancelled: bookings.filter(b => b.status === 'cancelled').length,
  };

  return (
    <PageContainer>
      <NavHeader>
        <NavContent>
          <BackButton onClick={() => navigate('/weddings')}>
            <FaArrowLeft />
            Back to Vendors
          </BackButton>
          <PageTitle>
            <FaCalendarAlt />
            My Wedding Bookings
          </PageTitle>
        </NavContent>
      </NavHeader>

      <Container>
        <StatsGrid>
          <StatCard color={theme.colors.primary}>
            <StatNumber>{stats.total}</StatNumber>
            <StatLabel>Total Bookings</StatLabel>
          </StatCard>
          <StatCard color={theme.colors.success}>
            <StatNumber>{stats.confirmed}</StatNumber>
            <StatLabel>Confirmed</StatLabel>
          </StatCard>
          <StatCard color={theme.colors.warning}>
            <StatNumber>{stats.pending}</StatNumber>
            <StatLabel>Pending</StatLabel>
          </StatCard>
          <StatCard color={theme.colors.error}>
            <StatNumber>{stats.cancelled}</StatNumber>
            <StatLabel>Cancelled</StatLabel>
          </StatCard>
        </StatsGrid>

        <FiltersSection>
          <FiltersRow>
            <FilterGroup>
              <SearchInput
                type="text"
                placeholder="Search bookings..."
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
              />
            </FilterGroup>

            <FilterGroup>
              <FilterButton
                active={statusFilter === 'all'}
                onClick={() => setStatusFilter('all')}
              >
                All Status
              </FilterButton>
              <FilterButton
                active={statusFilter === 'confirmed'}
                onClick={() => setStatusFilter('confirmed')}
              >
                Confirmed
              </FilterButton>
              <FilterButton
                active={statusFilter === 'pending'}
                onClick={() => setStatusFilter('pending')}
              >
                Pending
              </FilterButton>
              <FilterButton
                active={statusFilter === 'cancelled'}
                onClick={() => setStatusFilter('cancelled')}
              >
                Cancelled
              </FilterButton>
            </FilterGroup>

            <FilterGroup>
              <SortSelect
                value={sortBy}
                onChange={e => setSortBy(e.target.value)}
              >
                <option value="date">Sort by Event Date</option>
                <option value="created">Sort by Created Date</option>
                <option value="status">Sort by Status</option>
              </SortSelect>
            </FilterGroup>
          </FiltersRow>
        </FiltersSection>

        {filteredBookings.length > 0 ? (
          <BookingsGrid>
            {filteredBookings.map(booking => {
              const vendor = getVendorDetails(booking.vendorId);
              return (
                <BookingCard key={booking.id} status={booking.status}>
                  <BookingHeader>
                    <BookingInfo>
                      <BookingNumber>Booking #{booking.id}</BookingNumber>
                      <BookingMeta>
                        <BookingStatus status={booking.status}>
                          {getStatusIcon(booking.status)}
                          {booking.status}
                        </BookingStatus>
                        <MetaItem>
                          <FaCalendarAlt />
                          {new Date(booking.date).toLocaleDateString('en-US', {
                            weekday: 'short',
                            month: 'short',
                            day: 'numeric',
                            year: 'numeric',
                          })}{' '}
                          at {booking.time}
                        </MetaItem>
                        <MetaItem>
                          <FaUser />
                          {booking.guestCount} guests
                        </MetaItem>
                      </BookingMeta>
                    </BookingInfo>
                  </BookingHeader>

                  <BookingContent>
                    {vendor && (
                      <VendorSection>
                        <VendorImage src={vendor.image} alt={vendor.name} />
                        <VendorInfo>
                          <VendorName>{vendor.name}</VendorName>
                          <VendorMeta>
                            <MetaItem>
                              <FaMapMarkerAlt />
                              {vendor.city}, {vendor.state}
                            </MetaItem>
                            <MetaItem>
                              <FaStar />
                              {vendor.rating} ({vendor.reviewCount} reviews)
                            </MetaItem>
                          </VendorMeta>
                        </VendorInfo>
                      </VendorSection>
                    )}

                    <BookingDetails>
                      <DetailGroup>
                        <DetailLabel>Contact Information</DetailLabel>
                        <DetailValue>
                          <div>{booking.contactName}</div>
                          <div>{booking.email}</div>
                          <div>{booking.phone}</div>
                        </DetailValue>
                      </DetailGroup>
                      <DetailGroup>
                        <DetailLabel>Event Type</DetailLabel>
                        <DetailValue>{booking.eventType}</DetailValue>
                      </DetailGroup>
                      <DetailGroup>
                        <DetailLabel>Services Requested</DetailLabel>
                        <DetailValue>{booking.services.join(', ')}</DetailValue>
                      </DetailGroup>
                      <DetailGroup>
                        <DetailLabel>Booking Created</DetailLabel>
                        <DetailValue>
                          {new Date(booking.createdAt).toLocaleDateString(
                            'en-US',
                            {
                              month: 'short',
                              day: 'numeric',
                              year: 'numeric',
                            }
                          )}
                        </DetailValue>
                      </DetailGroup>
                    </BookingDetails>

                    <BookingActions>
                      <ActionButton onClick={() => handleViewBooking(booking)}>
                        <FaEye />
                        View Details
                      </ActionButton>
                      {vendor && (
                        <ActionButton
                          onClick={() => handleViewVendor(vendor.id)}
                        >
                          <FaRing />
                          View Vendor
                        </ActionButton>
                      )}
                      {booking.status === 'pending' && (
                        <ActionButton danger>
                          <FaTimesCircle />
                          Cancel Booking
                        </ActionButton>
                      )}
                    </BookingActions>
                  </BookingContent>
                </BookingCard>
              );
            })}
          </BookingsGrid>
        ) : (
          <EmptyState>
            <h3>No bookings found</h3>
            <p>
              {statusFilter !== 'all'
                ? `No ${statusFilter} bookings match your criteria.`
                : "You haven't made any wedding bookings yet."}
            </p>
            <ActionButton primary onClick={() => navigate('/weddings')}>
              <FaRing />
              Browse Wedding Vendors
            </ActionButton>
          </EmptyState>
        )}
      </Container>
    </PageContainer>
  );
};

export default MyBookings;

