              <NavSectionTitle>{section}</NavSectionTitle>
              {items.map(item => (
                <NavItem
                  key={item.id}
                  active={activeSection === item.id}
                  onClick={() => {
                    setActiveSection(item.id);
                    closeMobileSidebar();
                  }}
                >
                  <item.icon />
                  {item.label}
                  <FaChevronRight
                    style={{ marginLeft: 'auto', fontSize: '0.8rem' }}
                  />
                </NavItem>
              ))}
            </NavSection>
          ))}

          {/* Save Actions Section - Now part of scrollable content */}
          <NavSection>
            <NavSectionTitle>Changes</NavSectionTitle>
            {changedSections.size > 0 && (
              <div
                style={{
                  padding: `0 ${theme.spacing.lg}`,
                  marginBottom: theme.spacing.md,
                }}
              >
                <p
                  style={{
                    fontSize: '0.85rem',
                    color: theme.colors.gray600,
                    margin: '0 0 8px 0',
                  }}
                >
                  Modified sections:
                </p>
                {Array.from(changedSections).map(sectionId => {
                  const section = navigationItems.find(
                    item => item.id === sectionId
                  );
                  return (
                    <div
                      key={sectionId}
                      style={{
                        fontSize: '0.8rem',
                        color: theme.colors.warning,
                        display: 'flex',
                        alignItems: 'center',
                        gap: '4px',
                        marginBottom: '4px',
                      }}
                    >
                      <FaEdit size={10} />
                      {section?.label || sectionId}
                    </div>
                  );
                })}
              </div>
            )}
          </NavSection>

          <NavSection>
            <NavSectionTitle>Actions</NavSectionTitle>
            <SidebarFooter>
              <SaveActionsContainer>
                {changedSections.size > 0 && !saved && (
                  <ChangesIndicator>
                    <FaEdit />
                    {changedSections.size} section
                    {changedSections.size > 1 ? 's' : ''} modified
                  </ChangesIndicator>
                )}
                {saved && (
                  <ChangesIndicator style={{ color: theme.colors.success }}>
                    <FaCheckCircle />
                    Changes saved - Ready to publish
                  </ChangesIndicator>
                )}
                <SaveButton variant="secondary" onClick={handleDiscardChanges}>
                  <FaUndo />
                  Discard
                </SaveButton>
                <SaveButton
                  onClick={handleSaveChanges}
                  disabled={changedSections.size === 0 || saved}
                  saved={saved}
                >
                  <FaSave />
                  {saved ? 'Saved' : 'Save Changes'}
                </SaveButton>
                <SaveButton
                  variant="primary"
                  onClick={handleSaveAndGoLive}
                  disabled={!saved}
                >
                  <FaCheckCircle />
                  Save & Go Live
                </SaveButton>
              </SaveActionsContainer>
            </SidebarFooter>
          </NavSection>
        </SidebarNav>
      </Sidebar>

      <MainContent>
        <ContentHeader>
          <div
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: theme.spacing.lg,
