                                <RemoveImageButton
                                  onClick={() =>
                                    updateCustomSectionCard(index, 'image', '')
                                  }
                                >
                                  <FaTrash />
                                </RemoveImageButton>
                              </ImageOverlay>
                            </ImagePreview>
                          )}
                          <FileUploadBox>
                            <input
                              type="file"
                              accept="image/*"
                              onChange={e => {
                                const file = e.target.files[0];
                                if (file) {
                                  const reader = new FileReader();
                                  reader.onload = event => {
                                    updateCustomSectionCard(
                                      index,
                                      'image',
                                      event.target.result
                                    );
                                  };
                                  reader.readAsDataURL(file);
                                }
