const VendorDetail = () => {
  const { vendorSlug } = useParams();
  const navigate = useNavigate();
  const [vendor, setVendor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isLiked, setIsLiked] = useState(false);

  useEffect(() => {
    const vendorData = getVendorById(vendorSlug);
    setVendor(vendorData);
    setLoading(false);
  }, [vendorSlug]);

  const handleBooking = () => {
    navigate(`/${vendorSlug}/booking`);
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: vendor.name,
        text: vendor.description,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      alert('Link copied to clipboard!');
    }
  };

  const mockReviews = [
    {
      id: 1,
      name: 'Sarah Johnson',
      rating: 5,
      date: '2 weeks ago',
      text: 'Absolutely amazing service! They made our wedding day perfect. The attention to detail was incredible and the team was so professional throughout the entire process.',
    },
    {
      id: 2,
      name: 'Michael & Emma',
      rating: 5,
      date: '1 month ago',
      text: "We couldn't have asked for better vendors. From the initial consultation to the final execution, everything was flawless. Highly recommend to any couple planning their special day!",
    },
    {
      id: 3,
      name: 'Jennifer Chen',
      rating: 4,
      date: '2 months ago',
      text: 'Great experience overall. Very responsive and accommodating to our needs. The quality of work exceeded our expectations and the pricing was fair.',
    },
  ];

  const services = [
    {
      name: 'Wedding Planning',
      description: 'Complete wedding planning from start to finish',
      icon: FaRing,
    },
    {
      name: 'Venue Decoration',
      description: 'Beautiful decorations to transform your venue',
      icon: FaLeaf,
    },
    {
      name: 'Floral Arrangements',
      description: 'Custom bouquets and centerpieces',
      icon: FaLeaf,
    },
    {
      name: 'Photography',
      description: 'Professional wedding photography services',
      icon: FaCamera,
    },
  ];

  if (loading) {
    return (
      <PageContainer>
        <LoadingState>Loading vendor details...</LoadingState>
      </PageContainer>
    );
  }

  if (!vendor) {
    return (
      <PageContainer>
        <NavHeader>
          <NavContent>
            <BackButton onClick={() => navigate('/weddings')}>
              <FaArrowLeft />
              Back to Vendors
            </BackButton>
          </NavContent>
        </NavHeader>
        <NotFound>
          <h2>Vendor Not Found</h2>
          <p>
            The vendor you're looking for doesn't exist or may have been
            removed.
          </p>
          <ActionButton primary onClick={() => navigate('/weddings')}>
            Back to Vendors
          </ActionButton>
        </NotFound>
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <NavHeader>
        <NavContent>
          <BackButton onClick={() => navigate('/weddings')}>
            <FaArrowLeft />
            Back to Vendors
          </BackButton>
          <ActionButtons>
            <ActionButton onClick={() => setIsLiked(!isLiked)}>
              <FaHeart
                style={{ color: isLiked ? theme.colors.error : 'inherit' }}
              />
              {isLiked ? 'Liked' : 'Like'}
            </ActionButton>
            <ActionButton onClick={handleShare}>
              <FaShare />
              Share
            </ActionButton>
            <ActionButton primary onClick={handleBooking}>
              <FaCalendarAlt />
              Book Now
            </ActionButton>
          </ActionButtons>
        </NavContent>
      </NavHeader>

      <Container>
        <HeroSection>
          <HeroImage src={vendor.image} alt={vendor.name} />
          <HeroContent>
            <VendorHeader>
              <VendorInfo>
                <VendorName>{vendor.name}</VendorName>
                <VendorMeta>
                  <MetaItem>
                    <FaMapMarkerAlt />
                    {vendor.address}
                  </MetaItem>
                  <Rating>
                    <FaStar />
                    {vendor.rating} ({vendor.reviewCount} reviews)
                  </Rating>
                  <MetaItem>
                    <FaMapMarkerAlt />
                    {vendor.distance}mi away
                  </MetaItem>
                </VendorMeta>
                <VendorDescription>{vendor.description}</VendorDescription>
                <SpecialtiesGrid>
                  {vendor.specialties.map((specialty, index) => (
                    <SpecialtyTag key={index}>{specialty}</SpecialtyTag>
                  ))}
                </SpecialtiesGrid>
              </VendorInfo>
              {vendor.featured && (
                <FeaturedBadge>Featured Vendor</FeaturedBadge>
              )}
            </VendorHeader>

            <ContactGrid>
              <ContactCard>
                <ContactIcon>
                  <FaPhoneAlt />
                </ContactIcon>
                <ContactLabel>Phone</ContactLabel>
                <ContactValue>{vendor.phone}</ContactValue>
              </ContactCard>
              <ContactCard>
                <ContactIcon>
                  <FaEnvelope />
                </ContactIcon>
                <ContactLabel>Email</ContactLabel>
                <ContactValue>{vendor.email}</ContactValue>
              </ContactCard>
              <ContactCard>
                <ContactIcon>
                  <FaGlobeAmericas />
                </ContactIcon>
                <ContactLabel>Website</ContactLabel>
                <ContactValue>{vendor.website}</ContactValue>
              </ContactCard>
            </ContactGrid>
          </HeroContent>
        </HeroSection>

        <ContentGrid>
          <MainContent>
            <Section>
              <SectionTitle>
                <FaCamera />
                Portfolio Gallery
              </SectionTitle>
              <GalleryGrid>
                {vendor.portfolioImages.map((image, index) => (
                  <GalleryItem
                    key={index}
                    src={image}
                    alt={`${vendor.name} portfolio ${index + 1}`}
                  />
                ))}
              </GalleryGrid>
            </Section>

            <Section>
              <SectionTitle>
                <FaQuoteLeft />
                Customer Reviews
              </SectionTitle>
              {mockReviews.map(review => (
                <ReviewCard key={review.id}>
                  <ReviewHeader>
                    <ReviewerInfo>
                      <ReviewerName>{review.name}</ReviewerName>
                      <ReviewDate>{review.date}</ReviewDate>
                    </ReviewerInfo>
                    <ReviewRating>
                      {Array.from({ length: review.rating }, (_, i) => (
                        <FaStar key={i} />
                      ))}
                    </ReviewRating>
                  </ReviewHeader>
                  <ReviewText>{review.text}</ReviewText>
                </ReviewCard>
              ))}
            </Section>

            <Section>
              <SectionTitle>
                <FaCheckCircle />
                Services Offered
              </SectionTitle>
              <ServicesList>
                {services.map((service, index) => (
                  <ServiceItem key={index}>
                    <ServiceIcon>
                      <service.icon />
                    </ServiceIcon>
                    <ServiceInfo>
                      <ServiceName>{service.name}</ServiceName>
                      <ServiceDescription>
                        {service.description}
                      </ServiceDescription>
                    </ServiceInfo>
                  </ServiceItem>
                ))}
              </ServicesList>
            </Section>
          </MainContent>

          <Sidebar>
            <BookingCard>
              <BookingTitle>Ready to Book?</BookingTitle>
              <BookingDescription>
                Get in touch with {vendor.name} to discuss your wedding plans
                and check availability for your special day.
              </BookingDescription>
              <BookingButton onClick={handleBooking}>
                <FaCalendarAlt />
                Request Consultation
              </BookingButton>
            </BookingCard>

            <Section>
              <SectionTitle>
                <FaClock />
                Business Hours
              </SectionTitle>
              <HoursGrid>
                {Object.entries(vendor.hours).map(([day, hours]) => (
                  <HourRow key={day}>
                    <Day>{day.charAt(0).toUpperCase() + day.slice(1)}</Day>
                    <Hours>{hours}</Hours>
                  </HourRow>
                ))}
              </HoursGrid>
            </Section>

            <Section>
              <SectionTitle>
                <FaInfo />
                Quick Info
              </SectionTitle>
              <ServicesList>
                <ServiceItem>
                  <ServiceIcon>
                    <FaMapMarkerAlt />
                  </ServiceIcon>
                  <ServiceInfo>
                    <ServiceName>Location</ServiceName>
                    <ServiceDescription>
                      {vendor.city}, {vendor.state}
                    </ServiceDescription>
                  </ServiceInfo>
                </ServiceItem>
                <ServiceItem>
                  <ServiceIcon>
                    <FaStar />
                  </ServiceIcon>
                  <ServiceInfo>
                    <ServiceName>Rating</ServiceName>
                    <ServiceDescription>
                      {vendor.rating}/5 from {vendor.reviewCount} reviews
                    </ServiceDescription>
                  </ServiceInfo>
                </ServiceItem>
                <ServiceItem>
                  <ServiceIcon>
                    <FaUsers />
                  </ServiceIcon>
                  <ServiceInfo>
                    <ServiceName>Specialties</ServiceName>
                    <ServiceDescription>
                      {vendor.specialties.length} services offered
                    </ServiceDescription>
                  </ServiceInfo>
                </ServiceItem>
              </ServicesList>
            </Section>
          </Sidebar>
        </ContentGrid>
      </Container>
    </PageContainer>
  );
};

export default VendorDetail;

