const VendorPage = () => {
  const { vendorSlug } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();

  // Get vendor ID from URL path if not available in params
  const currentPath = window.location.pathname;
  const pathSegments = currentPath.split('/').filter(Boolean);
  const vendorId = vendorSlug || pathSegments[pathSegments.length - 1];

  // Get vendor data from Redux store for real-time updates
  const { vendors, editingVendor } = useSelector(
    state => state.vendorManagement
  );

  const [vendor, setVendor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [scrolled, setScrolled] = useState(false);
  const [showBackToTop, setShowBackToTop] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [activeGalleryTab, setActiveGalleryTab] = useState('decor');
  const [openFaq, setOpenFaq] = useState(null);
  const [contactForm, setContactForm] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
  });
  const [submitting, setSubmitting] = useState(false);

  useEffect(() => {
    let vendorData = null;

    // Priority 1: Use editing vendor data for real-time updates during editing
    if (editingVendor && editingVendor.id === vendorId) {
      vendorData = editingVendor;
      console.log(
        'Using editing vendor data for real-time updates:',
        editingVendor
      );
    }
    // Priority 2: Use saved vendor data from Redux vendors array
    else if (vendors && vendors.length > 0) {
      vendorData = vendors.find(v => v.id === vendorId);
      console.log('Using saved vendor data from Redux:', vendorData);
    }
    // Priority 3: Fallback to dummy data
    if (!vendorData) {
      vendorData = getVendorById(vendorId);
      console.log('Using fallback dummy data:', vendorData);
    }

    if (vendorData) {
      setVendor(vendorData);
      console.log('VendorPage: Updated vendor data', vendorData);
    }
    setLoading(false);

    // Pre-fill form if user is logged in
    if (user) {
      setContactForm(prev => ({
        ...prev,
        name: user.name || '',
        email: user.email || '',
      }));
    }
  }, [vendorId, user, vendors, editingVendor]);

  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      setScrolled(scrollPosition > 100);
      setShowBackToTop(scrollPosition > 500);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const scrollToSection = sectionId => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    setMobileMenuOpen(false); // Close mobile menu after navigation
  };

  const closeMobileMenu = () => setMobileMenuOpen(false);

  // Close mobile menu on escape key
  useEffect(() => {
    const handleEscapeKey = event => {
      if (event.key === 'Escape' && mobileMenuOpen) {
        closeMobileMenu();
      }
    };

    document.addEventListener('keydown', handleEscapeKey);
    return () => document.removeEventListener('keydown', handleEscapeKey);
  }, [mobileMenuOpen]);

  // Prevent body scroll when mobile menu is open
  useEffect(() => {
    if (mobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [mobileMenuOpen]);

  const handleContactSubmit = async e => {
    e.preventDefault();
    setSubmitting(true);

    // Simulate API call
    setTimeout(() => {
      alert('Thank you for your enquiry! We will get back to you soon.');
      setContactForm({ name: '', email: '', phone: '', message: '' });
      setSubmitting(false);
    }, 1000);
  };

  const handleInputChange = e => {
    const { name, value } = e.target;
    setContactForm(prev => ({ ...prev, [name]: value }));
  };

  const toggleFaq = index => {
    setOpenFaq(openFaq === index ? null : index);
  };

  if (loading) {
    return (
      <LoadingContainer>
        <LoadingSpinner>
          <FaSpinner />
        </LoadingSpinner>
      </LoadingContainer>
    );
  }

  if (!vendor) {
    return (
      <LoadingContainer>
        <div style={{ textAlign: 'center' }}>
          <h2>Vendor Not Found</h2>
          <p>The vendor you're looking for doesn't exist.</p>
          <NavButton onClick={() => navigate('/weddings')}>
            <FaArrowLeft />
            Back to Vendors
          </NavButton>
        </div>
      </LoadingContainer>
    );
  }

  const primaryColor = vendor.theme?.primaryColor || theme.colors.primary;

  return (
    <PageContainer>
      <NavBar scrolled={scrolled}>
        <NavContent>
          <NavLogo scrolled={scrolled}>{vendor.name}</NavLogo>
          <NavActions isOpen={mobileMenuOpen}>
            <MobileCloseButton onClick={closeMobileMenu}>
              <FaTimes />
            </MobileCloseButton>
            <NavButton
              onClick={() => {
                navigate('/weddings');
                closeMobileMenu();
              }}
              scrolled={scrolled}
            >
              <FaArrowLeft />
              Back
            </NavButton>
            <NavButton
              onClick={() => {
                alert('Vendor saved to your favorites!');
                closeMobileMenu();
              }}
              scrolled={scrolled}
            >
              <FaHeart />
              Save
            </NavButton>
            <NavButton
              onClick={() => {
                navigator.share
                  ? navigator.share({
                      title: vendor.name,
                      url: window.location.href,
                    })
                  : alert('Share: ' + window.location.href);
                closeMobileMenu();
              }}
              scrolled={scrolled}
            >
              <FaShare />
              Share
            </NavButton>
            <NavButton
              onClick={() => {
                navigate(`/${vendorId}/portfolio`);
                closeMobileMenu();
              }}
              scrolled={scrolled}
            >
              <FaImages />
              Portfolio
            </NavButton>
            <NavButton
              onClick={() => {
                navigate(`/${vendorId}/weddingadminpanel`);
                closeMobileMenu();
              }}
              scrolled={scrolled}
            >
              <FaCog />
              Dashboard
            </NavButton>
            <NavButton
              primary
              primaryColor={primaryColor}
              onClick={() => {
                scrollToSection('contact');
                closeMobileMenu();
              }}
            >
              <FaCalendarAlt />
              Enquire Now
            </NavButton>
          </NavActions>
          <MobileMenuButton
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            scrolled={scrolled}
            aria-label={mobileMenuOpen ? 'Close menu' : 'Open menu'}
          >
            {mobileMenuOpen ? <FaTimes /> : <FaBars />}
          </MobileMenuButton>
        </NavContent>
      </NavBar>

      {/* Dynamic Sections Based on Order */}
      {(() => {
        const defaultOrder = [
          'hero',
          'about-us',
          'services-offered',
          'recent-work',
          'gallery',
          'packages-pricing',
          'testimonials',
        ];

        const sectionOrder = vendor.sectionOrder || defaultOrder;

        // Combine default sections with custom sections
        const allSections = [...sectionOrder];

        // Add custom sections to the end if not already included
        if (vendor.customSections) {
          vendor.customSections.forEach(customSection => {
            const customId = `custom-${customSection.id}`;
            if (!allSections.includes(customId)) {
              allSections.push(customId);
            }
          });
        }

        return allSections.map(sectionId => {
          // Check if this is a custom section
          if (sectionId.startsWith('custom-')) {
            const customId = sectionId.replace('custom-', '');
            const customSection = vendor.customSections?.find(
              cs => cs.id === customId
            );

            if (!customSection) return null;

            // Check custom section visibility
            if (
              vendor.customSectionVisibility &&
              vendor.customSectionVisibility[customId] === false
            ) {
              return null;
            }

            // Also check the visible field directly on the custom section
            if (customSection.visible === false) {
              return null;
            }

            return (
              <Section
                key={`custom-${customSection.id}`}
                id={`custom-${customSection.id}`}
              >
                <Container>
                  <SectionTitle>{customSection.title}</SectionTitle>
                  {customSection.subtitle && (
                    <SectionSubtitle>{customSection.subtitle}</SectionSubtitle>
                  )}

                  {/* Render custom section content based on type */}
                  {customSection.type === 'text' && customSection.content && (
                    <div
                      style={{
                        fontSize: '1.1rem',
                        lineHeight: 1.7,
                        color: theme.colors.gray700,
                        textAlign: 'center',
                        maxWidth: '800px',
                        margin: '0 auto',
                        whiteSpace: 'pre-line',
                      }}
                    >
                      {customSection.content}
                    </div>
                  )}

                  {customSection.type === 'gallery' &&
                    customSection.images &&
                    customSection.images.length > 0 && (
                      <GalleryGrid>
                        {customSection.images.map((image, idx) => (
                          <GalleryItem
                            key={idx}
                            src={image}
                            alt={`${customSection.title} ${idx + 1}`}
                          />
                        ))}
                      </GalleryGrid>
                    )}

                  {customSection.type === 'cards' &&
                    customSection.cards &&
                    customSection.cards.length > 0 && (
                      <ServicesGrid>
                        {customSection.cards.map((card, idx) => (
                          <ServiceCard key={idx}>
                            {card.image && (
                              <ServiceImage src={card.image} alt={card.title} />
                            )}
                            <ServiceContent>
                              {card.icon && (
                                <ServiceIcon>{card.icon}</ServiceIcon>
                              )}
                              <ServiceName>{card.title}</ServiceName>
                              <ServiceDescription>
                                {card.description}
                              </ServiceDescription>
                            </ServiceContent>
                          </ServiceCard>
                        ))}
                      </ServicesGrid>
                    )}
                </Container>
              </Section>
            );
          }

          // Handle default sections
          // Check section visibility for default sections
          if (
            vendor.sectionVisibility &&
            vendor.sectionVisibility[sectionId] === false
          ) {
            return null;
          }

          switch (sectionId) {
            case 'hero':
              return (
                <HeroSection
                  key="hero"
                  id="hero"
                  backgroundImage={vendor.image}
                >
                  {vendor.heroVideo && (
                    <HeroMedia>
                      <HeroVideo
                        src={vendor.heroVideo}
                        allow="autoplay; fullscreen; picture-in-picture"
                        allowFullScreen
                      />
                    </HeroMedia>
                  )}
                  <HeroOverlay />
                  <HeroContent>
                    <HeroTitle>{vendor.name}</HeroTitle>
                    <HeroTagline>{vendor.tagline}</HeroTagline>
                    <HeroButton
                      primaryColor={primaryColor}
                      onClick={() => scrollToSection('contact')}
                    >
                      <FaCalendarAlt />
                      Enquire Now
                    </HeroButton>
                  </HeroContent>
                </HeroSection>
              );

            case 'about-us':
              return (
                <Section
                  key="about"
                  id="about"
                  backgroundColor={vendor.theme?.backgroundColor}
                >
                  <Container>
                    <SectionTitle>About Us</SectionTitle>
                    <AboutGrid>
                      <AboutContent>
                        <AboutText>
                          {vendor.aboutUs?.text || vendor.description}
                        </AboutText>
                        <AboutStats>
                          {vendor.aboutUs?.experienceVisible !== false &&
                            vendor.aboutUs?.experience && (
                              <StatCard>
                                <StatNumber primaryColor={primaryColor}>
                                  {vendor.aboutUs.experience}
                                </StatNumber>
                                <StatLabel>Experience</StatLabel>
                              </StatCard>
                            )}
                          {vendor.aboutUs?.weddingsVisible !== false &&
                            vendor.aboutUs?.completedWeddings && (
                              <StatCard>
                                <StatNumber primaryColor={primaryColor}>
                                  {vendor.aboutUs.completedWeddings}
                                </StatNumber>
                                <StatLabel>Weddings</StatLabel>
                              </StatCard>
                            )}
                          {vendor.aboutUs?.couplesVisible !== false &&
                            vendor.aboutUs?.satisfiedCouples && (
                              <StatCard>
                                <StatNumber primaryColor={primaryColor}>
                                  {vendor.aboutUs.satisfiedCouples}
                                </StatNumber>
                                <StatLabel>Happy Couples</StatLabel>
                              </StatCard>
                            )}
                        </AboutStats>
                      </AboutContent>
                      {(vendor.aboutUs?.aboutImage ||
                        vendor.aboutUs?.videoEmbed) && (
                        <VideoContainer>
                          {vendor.aboutUs?.aboutImage ? (
                            <img
                              src={vendor.aboutUs.aboutImage}
                              alt="About us"
                              style={{
                                width: '100%',
                                height: '100%',
                                objectFit: 'cover',
                                borderRadius: '8px',
                              }}
                            />
                          ) : vendor.aboutUs?.videoEmbed ? (
                            <VideoEmbed
                              src={vendor.aboutUs.videoEmbed}
                              allow="autoplay; fullscreen; picture-in-picture"
                              allowFullScreen
                            />
                          ) : null}
                        </VideoContainer>
                      )}
                    </AboutGrid>
                  </Container>
                </Section>
              );

            case 'services-offered':
              return (
                <Section key="services" id="services">
                  <Container>
                    <SectionTitle>Services Offered</SectionTitle>
                    <SectionSubtitle>
                      We provide comprehensive wedding services to make your
                      special day perfect
                    </SectionSubtitle>
                    <ServicesGrid>
                      {vendor.services?.map((service, index) => (
                        <ServiceCard key={index}>
                          <ServiceImage
                            src={service.image}
                            alt={service.name}
                          />
                          <ServiceContent>
                            <ServiceIcon>{service.icon}</ServiceIcon>
                            <ServiceName>{service.name}</ServiceName>
                            <ServiceDescription>
                              {service.description}
                            </ServiceDescription>
                          </ServiceContent>
                        </ServiceCard>
                      ))}
                    </ServicesGrid>
                  </Container>
                </Section>
              );

            case 'recent-work':
              return vendor.locationPortfolio &&
                vendor.locationPortfolio.length > 0 ? (
                <Section key="portfolio" id="portfolio">
                  <Container>
                    <SectionTitle>Our Recent Work</SectionTitle>
                    <SectionSubtitle>
                      Explore some of our beautiful weddings across different
                      venues and locations
                    </SectionSubtitle>
                    <div
                      style={{
                        display: 'grid',
                        gridTemplateColumns:
                          'repeat(auto-fit, minmax(300px, 1fr))',
                        gap: theme.spacing.xl,
                        marginBottom: theme.spacing.xl,
                      }}
                    >
                      {vendor.locationPortfolio.slice(0, 3).map(portfolio => (
                        <div
                          key={portfolio.id}
                          style={{
                            background: theme.colors.white,
                            borderRadius: theme.borderRadius.lg,
                            overflow: 'hidden',
                            boxShadow: theme.shadows.md,
                            transition: 'all 0.3s ease',
                            cursor: 'pointer',
                          }}
                          onClick={() => navigate(`/${vendorId}/portfolio`)}
                        >
                          <img
                            src={portfolio.coverImage}
                            alt={portfolio.location}
                            style={{
                              width: '100%',
                              height: '200px',
                              objectFit: 'cover',
                            }}
                          />
                          <div style={{ padding: theme.spacing.lg }}>
                            <h4
                              style={{
                                margin: `0 0 ${theme.spacing.sm} 0`,
                                fontSize: '1.2rem',
                                fontWeight: 600,
                                color: theme.colors.gray900,
                              }}
                            >
                              {portfolio.location}
                            </h4>
                            <p
                              style={{
                                margin: `0 0 ${theme.spacing.sm} 0`,
                                color: theme.colors.gray600,
                                fontSize: '0.9rem',
                              }}
                            >
                              {portfolio.city}, {portfolio.state} {' '}
                              {portfolio.weddingDate}
                            </p>
                            <p
                              style={{
                                margin: 0,
                                color: theme.colors.gray700,
                                fontSize: '0.9rem',
                                lineHeight: 1.5,
                              }}
                            >
                              {portfolio.description.substring(0, 120)}...
                            </p>
                          </div>
                        </div>
                      ))}
                    </div>
                    <div style={{ textAlign: 'center' }}>
                      <button
                        onClick={() => navigate(`/${vendorId}/portfolio`)}
                        style={{
                          background: primaryColor,
                          color: 'white',
                          border: 'none',
                          padding: `${theme.spacing.md} ${theme.spacing.xl}`,
                          borderRadius: theme.borderRadius.md,
                          fontSize: '1.1rem',
                          fontWeight: 600,
                          cursor: 'pointer',
                          transition: 'all 0.3s ease',
                          display: 'flex',
                          alignItems: 'center',
                          gap: theme.spacing.sm,
                          margin: '0 auto',
                        }}
                        onMouseOver={e =>
                          (e.target.style.transform = 'translateY(-2px)')
                        }
                        onMouseOut={e =>
                          (e.target.style.transform = 'translateY(0)')
                        }
                      >
                        <FaImages />
                        View Complete Portfolio
                      </button>
                    </div>
                  </Container>
                </Section>
              ) : null;

            case 'gallery':
              return (
                <Section
                  key="gallery"
                  id="gallery"
                  backgroundColor={theme.colors.gray50}
                >
                  <Container>
                    <SectionTitle>Gallery</SectionTitle>
                    <SectionSubtitle>
                      Browse through our portfolio of beautiful weddings and
                      events
                    </SectionSubtitle>
                    <GalleryTabs>
                      {Object.keys(vendor.gallery || {}).map(category => {
                        const categoryData = vendor.gallery[category];
                        const categoryTitle =
                          categoryData?.title ||
                          category.charAt(0).toUpperCase() + category.slice(1);

                        return (
                          <GalleryTab
                            key={category}
                            active={activeGalleryTab === category}
                            primaryColor={primaryColor}
                            onClick={() => setActiveGalleryTab(category)}
                          >
                            <FaImages />
                            {categoryTitle}
                          </GalleryTab>
                        );
                      })}
                    </GalleryTabs>
                    <GalleryGrid>
                      {(() => {
                        const activeCategory =
                          vendor.gallery?.[activeGalleryTab];
                        const images = Array.isArray(activeCategory)
                          ? activeCategory
                          : activeCategory?.images || [];

                        return images.map((image, index) => (
                          <GalleryItem
                            key={index}
                            src={image}
                            alt={`${vendor.name} ${activeGalleryTab} ${index + 1}`}
                          />
                        ));
                      })()}
                    </GalleryGrid>
                  </Container>
                </Section>
              );

            case 'packages-pricing':
              return vendor.packages ? (
                <Section key="packages" id="packages">
                  <Container>
                    <SectionTitle>Packages & Pricing</SectionTitle>
                    <SectionSubtitle>
                      Choose the perfect package for your wedding celebration
                    </SectionSubtitle>
                    <PackagesGrid>
                      {vendor.packages.map((pkg, index) => (
                        <PackageCard key={index} primaryColor={primaryColor}>
                          <PackageName>{pkg.name}</PackageName>
                          <PackagePrice primaryColor={primaryColor}>
                            {pkg.price}
                          </PackagePrice>
                          <PackageDescription>
                            {pkg.description}
                          </PackageDescription>
                          <PackageFeatures>
                            {pkg.features.map((feature, featureIndex) => (
                              <PackageFeature
                                key={featureIndex}
                                primaryColor={primaryColor}
                              >
                                {feature}
                              </PackageFeature>
                            ))}
                          </PackageFeatures>
                          <PackageButton
                            primaryColor={primaryColor}
                            onClick={() => scrollToSection('contact')}
                          >
                            Get a Quote
                          </PackageButton>
                        </PackageCard>
                      ))}
                    </PackagesGrid>
                  </Container>
                </Section>
              ) : null;

            case 'testimonials':
              return (
                <Section
                  key="testimonials"
                  id="testimonials"
                  backgroundColor={vendor.theme?.backgroundColor}
                >
                  <Container>
                    <SectionTitle>What Our Couples Say</SectionTitle>
                    <SectionSubtitle>
                      Real testimonials from couples whose special day we helped
                      create
                    </SectionSubtitle>
                    <TestimonialsGrid>
                      {vendor.testimonials?.map((testimonial, index) => (
                        <TestimonialCard
                          key={index}
                          primaryColor={primaryColor}
                        >
                          <TestimonialText>{testimonial.text}</TestimonialText>
                          <TestimonialAuthor>
                            <AuthorInfo>
                              <AuthorName>{testimonial.name}</AuthorName>
                              <AuthorWedding>
                                {testimonial.wedding}
                              </AuthorWedding>
                            </AuthorInfo>
                            <TestimonialRating>
                              {Array.from(
                                { length: testimonial.rating },
                                (_, i) => (
                                  <FaStar key={i} />
                                )
                              )}
                            </TestimonialRating>
                          </TestimonialAuthor>
                        </TestimonialCard>
                      ))}
                    </TestimonialsGrid>
                  </Container>
                </Section>
              );

            default:
              return null;
          }
        });
      })()}

      {/* FAQ Section */}
      <Section id="faq">
        <Container>
          <SectionTitle>Frequently Asked Questions</SectionTitle>
          <SectionSubtitle>
            Find answers to common questions about our services
          </SectionSubtitle>
          <FaqContainer>
            {vendor.faq?.map((item, index) => (
              <FaqItem key={index}>
                <FaqQuestion onClick={() => toggleFaq(index)}>
                  {item.question}
                  {openFaq === index ? <FaChevronUp /> : <FaChevronDown />}
                </FaqQuestion>
                <FaqAnswer open={openFaq === index}>{item.answer}</FaqAnswer>
              </FaqItem>
            ))}
          </FaqContainer>
        </Container>
      </Section>

      {/* Contact Section */}
      <Section id="contact" backgroundColor={theme.colors.gray50}>
        <Container>
          <SectionTitle>Get In Touch</SectionTitle>
          <SectionSubtitle>
            Ready to start planning your dream wedding? Send us a message!
          </SectionSubtitle>
          <ContactGrid>
            <ContactForm onSubmit={handleContactSubmit}>
              <FormGroup>
                <FormLabel>Name *</FormLabel>
                <FormInput
                  type="text"
                  name="name"
                  value={contactForm.name}
                  onChange={handleInputChange}
                  required
                  primaryColor={primaryColor}
                />
              </FormGroup>
              <FormGroup>
                <FormLabel>Email *</FormLabel>
                <FormInput
                  type="email"
                  name="email"
                  value={contactForm.email}
                  onChange={handleInputChange}
                  required
                  primaryColor={primaryColor}
                />
              </FormGroup>
              <FormGroup>
                <FormLabel>Phone</FormLabel>
                <FormInput
                  type="tel"
                  name="phone"
                  value={contactForm.phone}
                  onChange={handleInputChange}
                  primaryColor={primaryColor}
                />
              </FormGroup>
              <FormGroup>
                <FormLabel>Message *</FormLabel>
                <FormTextarea
                  name="message"
                  value={contactForm.message}
                  onChange={handleInputChange}
                  placeholder="Tell us about your special day..."
                  required
                  primaryColor={primaryColor}
                />
              </FormGroup>
              <SubmitButton
                type="submit"
                disabled={submitting}
                primaryColor={primaryColor}
              >
                {submitting ? (
                  <FaSpinner className="spinning" />
                ) : (
                  <FaEnvelope />
                )}
                {submitting ? 'Sending...' : 'Send Enquiry'}
              </SubmitButton>
            </ContactForm>

            <ContactInfo>
              <ContactItem>
                <ContactIcon primaryColor={primaryColor}>
                  <FaPhoneAlt />
                </ContactIcon>
                <ContactDetails>
                  <ContactLabel>Phone</ContactLabel>
                  <ContactValue>{vendor.phone}</ContactValue>
                </ContactDetails>
              </ContactItem>

              <ContactItem>
                <ContactIcon primaryColor={primaryColor}>
                  <FaEnvelope />
                </ContactIcon>
                <ContactDetails>
                  <ContactLabel>Email</ContactLabel>
                  <ContactValue>{vendor.email}</ContactValue>
                </ContactDetails>
              </ContactItem>

              <ContactItem>
                <ContactIcon primaryColor={primaryColor}>
                  <FaMapMarkerAlt />
                </ContactIcon>
                <ContactDetails>
                  <ContactLabel>Address</ContactLabel>
                  <ContactValue>{vendor.address}</ContactValue>
                </ContactDetails>
              </ContactItem>

              <ContactItem>
                <ContactIcon primaryColor={primaryColor}>
                  <FaClock />
                </ContactIcon>
                <ContactDetails>
                  <ContactLabel>Business Hours</ContactLabel>
                  <ContactValue>Mon-Fri: 9AM-6PM</ContactValue>
                </ContactDetails>
              </ContactItem>

              <div>
                <ContactLabel>Cities We Cover</ContactLabel>
                <CitiesList>
                  {vendor.citiesCovered?.map((city, index) => (
                    <CityTag key={index} primaryColor={primaryColor}>
                      {city}
                    </CityTag>
                  ))}
                </CitiesList>
              </div>

              {vendor.mapEmbed && (
                <MapContainer>
                  <MapEmbed
                    src={vendor.mapEmbed}
                    allowFullScreen
                    loading="lazy"
                  />
                </MapContainer>
              )}
            </ContactInfo>
          </ContactGrid>
        </Container>
      </Section>

      {/* Footer */}
      <Footer>
        <Container>
          <FooterContent>
            <FooterSection>
              <FooterTitle>{vendor.name}</FooterTitle>
              <FooterText>{vendor.description}</FooterText>
              <SocialLinks>
                {vendor.socialLinks?.instagram && (
                  <SocialLink
                    href={vendor.socialLinks.instagram}
                    primaryColor={primaryColor}
                  >
                    <FaInstagram />
                  </SocialLink>
                )}
                {vendor.socialLinks?.facebook && (
                  <SocialLink
                    href={vendor.socialLinks.facebook}
                    primaryColor={primaryColor}
                  >
                    <FaFacebook />
                  </SocialLink>
                )}
                {vendor.socialLinks?.pinterest && (
                  <SocialLink
                    href={vendor.socialLinks.pinterest}
                    primaryColor={primaryColor}
                  >
                    <FaPinterest />
                  </SocialLink>
                )}
              </SocialLinks>
            </FooterSection>

            <FooterSection>
              <FooterTitle>Contact Info</FooterTitle>
              <FooterText>
                {vendor.phone}
                <br />
                {vendor.email}
                <br />
                {vendor.address}
              </FooterText>
            </FooterSection>

            <FooterSection>
              <FooterTitle>Quick Links</FooterTitle>
              <FooterText>
                <a
                  href="#about"
                  style={{
                    color: theme.colors.gray300,
                    textDecoration: 'none',
                  }}
                >
                  About Us
                </a>
                <br />
                <a
                  href="#services"
                  style={{
                    color: theme.colors.gray300,
                    textDecoration: 'none',
                  }}
                >
                  Services
                </a>
                <br />
                <a
                  href="#gallery"
                  style={{
                    color: theme.colors.gray300,
                    textDecoration: 'none',
                  }}
                >
                  Gallery
                </a>
                <br />
                <a
                  href="#contact"
                  style={{
                    color: theme.colors.gray300,
                    textDecoration: 'none',
                  }}
                >
                  Contact
                </a>
              </FooterText>
            </FooterSection>
          </FooterContent>

          <FooterBottom>
            <p>
              &copy; 2024 {vendor.name}. All rights reserved. | Terms &
              Conditions | Privacy Policy
            </p>
          </FooterBottom>
        </Container>
      </Footer>

      {/* Back to Top Button */}
      <BackToTopButton
        visible={showBackToTop}
        primaryColor={primaryColor}
        onClick={scrollToTop}
      >

      </BackToTopButton>
    </PageContainer>
  );
};

