const VendorPortfolio = () => {
  const { vendorSlug } = useParams();
  const navigate = useNavigate();

  // Get vendor ID from URL path if not available in params
  const currentPath = window.location.pathname;
  const pathSegments = currentPath.split('/').filter(Boolean);
  const vendorId = vendorSlug || pathSegments[0];

  // Get vendor data from Redux store for real-time updates
  const { vendors, editingVendor } = useSelector(
    state => state.vendorManagement
  );

  const [vendor, setVendor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedPortfolio, setSelectedPortfolio] = useState(null);
  const [imageViewer, setImageViewer] = useState({
    open: false,
    images: [],
    currentIndex: 0,
  });

  useEffect(() => {
    let vendorData = null;

    // Priority 1: Use editing vendor data for real-time updates during editing
    if (editingVendor && editingVendor.id === vendorId) {
      vendorData = editingVendor;
      console.log(
        'VendorPortfolio: Using editing vendor data for real-time updates:',
        editingVendor
      );
    }
    // Priority 2: Use saved vendor data from Redux vendors array
    else if (vendors && vendors.length > 0) {
      vendorData = vendors.find(v => v.id === vendorId);
      console.log(
        'VendorPortfolio: Using saved vendor data from Redux:',
        vendorData
      );
    }
    // Priority 3: Fallback to dummy data
    if (!vendorData) {
      vendorData = getVendorById(vendorId);
      console.log('VendorPortfolio: Using fallback dummy data:', vendorData);
    }

    if (vendorData) {
      setVendor(vendorData);
      console.log(
        'VendorPortfolio: Updated vendor data with locationPortfolio:',
        vendorData.locationPortfolio
      );
    }
    setLoading(false);
  }, [vendorId, vendors, editingVendor]);

  const openPortfolioModal = portfolio => {
    setSelectedPortfolio(portfolio);
  };

  const closePortfolioModal = () => {
    setSelectedPortfolio(null);
  };

  const openImageViewer = (images, startIndex = 0) => {
    setImageViewer({ open: true, images, currentIndex: startIndex });
  };

  const closeImageViewer = () => {
    setImageViewer({ open: false, images: [], currentIndex: 0 });
  };

  const nextImage = () => {
    setImageViewer(prev => ({
      ...prev,
      currentIndex: (prev.currentIndex + 1) % prev.images.length,
    }));
  };

  const prevImage = () => {
    setImageViewer(prev => ({
      ...prev,
      currentIndex:
        prev.currentIndex === 0
          ? prev.images.length - 1
          : prev.currentIndex - 1,
    }));
  };

  const handleKeyPress = e => {
    if (e.key === 'Escape') {
      if (imageViewer.open) {
        closeImageViewer();
      } else if (selectedPortfolio) {
        closePortfolioModal();
      }
    } else if (imageViewer.open) {
      if (e.key === 'ArrowLeft') prevImage();
      if (e.key === 'ArrowRight') nextImage();
    }
  };

  useEffect(() => {
    document.addEventListener('keydown', handleKeyPress);
    return () => document.removeEventListener('keydown', handleKeyPress);
  }, [imageViewer.open, selectedPortfolio]);

  if (loading) {
    return (
      <PageContainer>
        <LoadingState>Loading portfolio...</LoadingState>
      </PageContainer>
    );
  }

  if (!vendor) {
    return (
      <PageContainer>
        <Container>
          <div style={{ textAlign: 'center', padding: '4rem 0' }}>
            <h2>Vendor Not Found</h2>
            <p>The vendor portfolio you're looking for doesn't exist.</p>
            <BackButton onClick={() => navigate('/weddings')}>
              <FaArrowLeft />
              Back to Vendors
            </BackButton>
          </div>
        </Container>
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <NavHeader>
        <NavContent>
          <BackButton onClick={() => navigate(`/${vendorId}`)}>
            <FaArrowLeft />
            Back to {vendor.name}
          </BackButton>
          <VendorTitle>{vendor.name} Portfolio</VendorTitle>
        </NavContent>
      </NavHeader>

      <Container>
        <PageHeader>
          <PageTitle>Our Work & Locations</PageTitle>
          <PageSubtitle>
            Explore our portfolio of beautiful weddings across different venues
            and locations. Each celebration tells a unique story and showcases
            our commitment to creating magical moments.
          </PageSubtitle>
        </PageHeader>

        {vendor.locationPortfolio && vendor.locationPortfolio.length > 0 ? (
          <PortfolioGrid>
            {vendor.locationPortfolio.map(portfolio => (
              <PortfolioCard
                key={portfolio.id}
                onClick={() => openPortfolioModal(portfolio)}
              >
                <PortfolioImage
                  src={portfolio.coverImage}
                  alt={portfolio.location}
                />
                <PortfolioContent>
                  <LocationHeader>
                    <LocationInfo>
                      <LocationName>{portfolio.location}</LocationName>
                      <LocationDetails>
                        <LocationDetail>
                          <FaMapMarkerAlt />
                          {portfolio.city}, {portfolio.state}
                        </LocationDetail>
                        <LocationDetail>
                          <FaCalendarAlt />
                          {portfolio.weddingDate}
                        </LocationDetail>
                      </LocationDetails>
                    </LocationInfo>
                    <CoupleInfo>
                      <FaUsers />
                      {portfolio.coupleNames}
                    </CoupleInfo>
                  </LocationHeader>

                  <PortfolioDescription>
                    {portfolio.description}
                  </PortfolioDescription>

                  <ServicesList>
                    {portfolio.services.slice(0, 3).map((service, index) => (
                      <ServiceTag key={index}>{service}</ServiceTag>
                    ))}
                    {portfolio.services.length > 3 && (
                      <ServiceTag>
                        +{portfolio.services.length - 3} more
                      </ServiceTag>
                    )}
                  </ServicesList>

                  <ViewGalleryButton>
                    View Full Gallery ({portfolio.gallery.length} photos)
                  </ViewGalleryButton>
                </PortfolioContent>
              </PortfolioCard>
            ))}
          </PortfolioGrid>
        ) : (
          <div style={{ textAlign: 'center', padding: '4rem 0' }}>
            <h3>Portfolio Coming Soon</h3>
            <p>
              We're updating our portfolio with recent work. Check back soon!
            </p>
          </div>
        )}
      </Container>

      {/* Portfolio Detail Modal */}
      {selectedPortfolio && (
        <ModalOverlay onClick={closePortfolioModal}>
          <ModalContent onClick={e => e.stopPropagation()}>
            <ModalHeader>
              <ModalTitle>{selectedPortfolio.location}</ModalTitle>
              <CloseButton onClick={closePortfolioModal}>
                <FaTimes />
              </CloseButton>
            </ModalHeader>
            <ModalBody>
              <div
                style={{
                  display: 'grid',
                  gridTemplateColumns:
                    window.innerWidth <= 768 ? '1fr' : '1fr 1fr',
                  gap: theme.spacing.xl,
                  marginBottom: theme.spacing.xl,
                }}
              >
                <div>
                  <h4
                    style={{
                      margin: `0 0 ${theme.spacing.md} 0`,
                      color: theme.colors.gray900,
                    }}
                  >
                    Wedding Details
                  </h4>
                  <LocationDetails style={{ gap: theme.spacing.sm }}>
                    <LocationDetail>
                      <FaMapMarkerAlt />
                      <strong>
                        {selectedPortfolio.city}, {selectedPortfolio.state}
                      </strong>
                    </LocationDetail>
                    <LocationDetail>
                      <FaCalendarAlt />
                      <strong>{selectedPortfolio.weddingDate}</strong>
                    </LocationDetail>
                    <LocationDetail>
                      <FaHeart style={{ color: theme.colors.error }} />
                      <strong>{selectedPortfolio.coupleNames}</strong>
                    </LocationDetail>
                  </LocationDetails>
                </div>
                <div>
                  <h4
                    style={{
                      margin: `0 0 ${theme.spacing.md} 0`,
                      color: theme.colors.gray900,
                    }}
                  >
                    Services Provided
                  </h4>
                  <ServicesList>
                    {selectedPortfolio.services.map((service, index) => (
                      <ServiceTag key={index}>{service}</ServiceTag>
                    ))}
                  </ServicesList>
                </div>
              </div>

              <p
                style={{
                  fontSize: '1.1rem',
                  lineHeight: 1.6,
                  color: theme.colors.gray700,
                  marginBottom: theme.spacing.xl,
                }}
              >
                {selectedPortfolio.description}
              </p>

              <WorkflowSection>
                <WorkflowTitle>
                  <FaCheckCircle style={{ color: theme.colors.green500 }} />
                  Project Highlights
                </WorkflowTitle>
                <HighlightsList>
                  {selectedPortfolio.highlights.map((highlight, index) => (
                    <HighlightItem key={index}>
                      <FaStar
                        style={{
                          color: theme.colors.warning,
                          fontSize: '0.9rem',
                        }}
                      />
                      <HighlightText>{highlight}</HighlightText>
                    </HighlightItem>
                  ))}
                </HighlightsList>
              </WorkflowSection>

              <GallerySection>
                <WorkflowTitle>Wedding Gallery</WorkflowTitle>
                <GalleryGrid>
                  {selectedPortfolio.gallery.map((image, index) => (
                    <GalleryImage
                      key={index}
                      src={image}
                      alt={`${selectedPortfolio.location} ${index + 1}`}
                      onClick={() =>
                        openImageViewer(selectedPortfolio.gallery, index)
                      }
                    />
                  ))}
                </GalleryGrid>
              </GallerySection>
            </ModalBody>
          </ModalContent>
        </ModalOverlay>
      )}

      {/* Image Viewer */}
      {imageViewer.open && (
        <ImageViewerOverlay onClick={closeImageViewer}>
          <ImageViewerContent onClick={e => e.stopPropagation()}>
            <ViewerImage
              src={imageViewer.images[imageViewer.currentIndex]}
              alt={`Gallery ${imageViewer.currentIndex + 1}`}
            />

            {imageViewer.images.length > 1 && (
              <>
                <ImageNavButton className="prev" onClick={prevImage}>
                  <FaChevronLeft />
                </ImageNavButton>
                <ImageNavButton className="next" onClick={nextImage}>
                  <FaChevronRight />
                </ImageNavButton>
              </>
            )}

            <ImageCounter>
              {imageViewer.currentIndex + 1} / {imageViewer.images.length}
            </ImageCounter>

            <CloseButton
              onClick={closeImageViewer}
              style={{
                position: 'absolute',
                top: 20,
                right: 20,
                background: 'rgba(0,0,0,0.7)',
                color: 'white',
              }}
            >
              <FaTimes />
            </CloseButton>
          </ImageViewerContent>
        </ImageViewerOverlay>
      )}
    </PageContainer>
  );
};

export default VendorPortfolio;

