const WeddingHome = () => {
  const navigate = useNavigate();
  const [vendors, setVendors] = useState([]);
  const [filteredVendors, setFilteredVendors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentLocation, setCurrentLocation] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');
  const [sortBy, setSortBy] = useState('distance');
  const [locationLoading, setLocationLoading] = useState(false);
  const [selectedCategories, setSelectedCategories] = useState(['venue']); // Default to venues checked
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const categories = [
    { name: 'Venues', icon: FaLeaf, count: 15, filter: 'venue' },
    { name: 'Photography', icon: FaCamera, count: 25, filter: 'photography' },
    { name: 'Catering', icon: FaBirthdayCake, count: 18, filter: 'catering' },
    { name: 'Music & DJ', icon: FaMusic, count: 12, filter: 'music' },
    { name: 'Floral', icon: FaLeaf, count: 20, filter: 'floral' },
    { name: 'Jewelry', icon: FaGem, count: 8, filter: 'jewelry' },
  ];

  const loadVendorsForLocation = useCallback(location => {
    const vendorsWithDistance = updateVendorsWithDistance(
      weddingVendors,
      location
    );
    setVendors(vendorsWithDistance);
    setFilteredVendors(vendorsWithDistance);
    setLoading(false);
  }, []);

  const initializeLocation = useCallback(async () => {
    setLoading(true);
    try {
      const location = await getCurrentLocation();
      setCurrentLocation(location);
      loadVendorsForLocation(location);
    } catch (error) {
      console.error('Failed to get location:', error);
      const defaultLocation = getDefaultLocation();
      setCurrentLocation(defaultLocation);
      loadVendorsForLocation(defaultLocation);
    }
  }, [loadVendorsForLocation]);

  const handleLocationSearch = async e => {
    e.preventDefault();
    if (!searchTerm.trim()) return;

    setLocationLoading(true);
    try {
      let newLocation;

      if (/^\d{5}$/.test(searchTerm.trim())) {
        newLocation = await getLocationFromZip(searchTerm.trim());
      } else {
        newLocation = await searchLocationByCity(searchTerm.trim());
      }

      setCurrentLocation(newLocation);
      loadVendorsForLocation(newLocation);
      setSearchTerm('');
    } catch (error) {
      alert('Location not found. Please try a different city or ZIP code.');
    } finally {
      setLocationLoading(false);
    }
  };

  const handleUseCurrentLocation = async () => {
    setLocationLoading(true);
    try {
      const location = await getCurrentLocation();
      setCurrentLocation(location);
      loadVendorsForLocation(location);
    } catch (error) {
      alert(
        'Unable to get your current location. Please check your browser settings.'
      );
    } finally {
      setLocationLoading(false);
    }
  };

  const applyFilters = useCallback(() => {
    let filtered = [...vendors];

    // Filter by selected categories (only if categories are selected)
    if (selectedCategories.length > 0) {
      filtered = filtered.filter(vendor =>
        vendor.specialties.some(specialty =>
          selectedCategories.some(category => {
            const categoryMap = {
              venue: [
                'venue',
                'wedding venue',
                'reception venue',
                'wedding planning',
              ],
              photography: [
                'photography',
                'photographer',
                'wedding photography',
              ],
              catering: [
                'catering',
                'food',
                'wedding catering',
                'reception catering',
              ],
              music: ['music', 'dj', 'band', 'entertainment'],
              floral: ['floral', 'flowers', 'floral arrangements', 'bouquet'],
              jewelry: [
                'jewelry',
                'rings',
                'wedding rings',
                'engagement rings',
              ],
            };

            const categoryTerms = categoryMap[category] || [category];
            return categoryTerms.some(term =>
              specialty.toLowerCase().includes(term.toLowerCase())
            );
          })
        )
      );
    }

    // Filter by featured status
    if (activeFilter === 'featured') {
      filtered = filtered.filter(vendor => vendor.featured);
    }

    // Sort vendors
    switch (sortBy) {
      case 'distance':
        filtered.sort((a, b) => a.distance - b.distance);
        break;
      case 'rating':
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case 'name':
        filtered.sort((a, b) => a.name.localeCompare(b.name));
        break;
      default:
        break;
    }

    setFilteredVendors(filtered);
  }, [vendors, activeFilter, sortBy, selectedCategories]);

  const handleVendorClick = vendor => {
    navigate(`/${vendor.id}`);
  };

  const handleCategoryClick = category => {
    const categoryFilter = category.filter;
    setSelectedCategories(prev => {
      if (prev.includes(categoryFilter)) {
        // If already selected, remove it (uncheck)
        return prev.filter(cat => cat !== categoryFilter);
      } else {
        // If not selected, add it (check)
        return [...prev, categoryFilter];
      }
    });
  };

  const closeMobileMenu = () => setMobileMenuOpen(false);

  // Close mobile menu on escape key
  useEffect(() => {
    const handleEscapeKey = event => {
      if (event.key === 'Escape' && mobileMenuOpen) {
        closeMobileMenu();
      }
    };

    document.addEventListener('keydown', handleEscapeKey);
    return () => document.removeEventListener('keydown', handleEscapeKey);
  }, [mobileMenuOpen]);

  // Prevent body scroll when mobile menu is open
  useEffect(() => {
    if (mobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [mobileMenuOpen]);

  useEffect(() => {
    initializeLocation();
  }, [initializeLocation]);

  useEffect(() => {
    applyFilters();
  }, [applyFilters]);

  if (loading) {
    return (
      <PageContainer>
        <NavHeader>
          <MobileMenuOverlay
            isOpen={mobileMenuOpen}
            onClick={closeMobileMenu}
          />
          <NavContent>
            <Logo>
              <FaRing /> Wedding Vendors
            </Logo>
            <NavActions isOpen={mobileMenuOpen}>
              <BackButton
                onClick={() => {
                  navigate('/');
                  closeMobileMenu();
                }}
              >
                <FaHome />
                Back to Home
              </BackButton>
            </NavActions>
            <MobileMenuButton
              onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
              aria-label={mobileMenuOpen ? 'Close menu' : 'Open menu'}
            >
              {mobileMenuOpen ? <FaTimes /> : <FaBars />}
            </MobileMenuButton>
          </NavContent>
        </NavHeader>
        <LoadingState>
          <div className="spinner"></div>
          <h3>Finding wedding vendors near you...</h3>
          <p>Please wait while we locate nearby wedding services.</p>
        </LoadingState>
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <NavHeader>
        <MobileMenuOverlay isOpen={mobileMenuOpen} onClick={closeMobileMenu} />
        <NavContent>
          <Logo>
            <FaRing /> Wedding Vendors
          </Logo>
          <NavActions isOpen={mobileMenuOpen}>
            <MobileCloseButton onClick={closeMobileMenu}>
              <FaTimes />
            </MobileCloseButton>
            <BackButton
              onClick={() => {
                navigate('/');
                closeMobileMenu();
              }}
            >
              <FaHome />
              Back to Home
            </BackButton>
          </NavActions>
          <MobileMenuButton
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label={mobileMenuOpen ? 'Close menu' : 'Open menu'}
          >
            {mobileMenuOpen ? <FaTimes /> : <FaBars />}
          </MobileMenuButton>
        </NavContent>
      </NavHeader>

      <Container>
        <HeroSection>
          <HeroTitle>
            <FaHeart color={theme.colors.primary} />
            Find Your Perfect Wedding Vendors
            <FaRing color={theme.colors.secondary || theme.colors.primary} />
          </HeroTitle>
          <HeroSubtitle>
            Discover amazing wedding professionals near you to make your special
            day unforgettable. From venues to photography, catering to music -
            we've got you covered.
          </HeroSubtitle>
          {currentLocation && (
            <LocationDisplay>
              <FaMapMarkerAlt />
              <span>
                {currentLocation.city}, {currentLocation.state}
                {currentLocation.isDefault && ' (Default Location)'}
              </span>
            </LocationDisplay>
          )}
        </HeroSection>

        <SearchSection>
          <SearchForm onSubmit={handleLocationSearch}>
            <SearchInputContainer>
              <SearchIcon>
                <FaSearch />
              </SearchIcon>
              <SearchInput
                type="text"
                placeholder="Enter city name or ZIP code to find vendors..."
                value={searchTerm}
                onChange={e => setSearchTerm(e.target.value)}
              />
            </SearchInputContainer>

            <LocationButton
              type="button"
              onClick={handleUseCurrentLocation}
              disabled={locationLoading}
            >
              <FaLocationArrow />
              Use My Location
            </LocationButton>

            <SearchButton type="submit" disabled={locationLoading}>
              {locationLoading ? 'Searching...' : 'Search'}
            </SearchButton>
          </SearchForm>
        </SearchSection>

        <CategorySection>
          <div
            style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: theme.spacing.lg,
            }}
          >
            <SectionTitle>Wedding Services</SectionTitle>
            <ActionButton onClick={() => setSelectedCategories([])}>
              Clear All
            </ActionButton>
          </div>
          <CategoryGrid>
            {categories.map((category, index) => {
              const isSelected = selectedCategories.includes(category.filter);
              return (
                <CategoryCard
                  key={index}
                  selected={isSelected}
                  onClick={() => handleCategoryClick(category)}
                >
                  <CategoryCheckbox checked={isSelected}>
                    <FaCheck />
                  </CategoryCheckbox>
                  <CategoryIcon>
                    <category.icon />
                  </CategoryIcon>
                  <CategoryName>{category.name}</CategoryName>
                  <CategoryCount>
                    {category.count} vendors available
                  </CategoryCount>
                </CategoryCard>
              );
            })}
          </CategoryGrid>
        </CategorySection>

        <VendorsSection>
          <SectionTitle>
            {selectedCategories.length > 0
              ? `${selectedCategories
                  .map(cat => {
                    const category = categories.find(c => c.filter === cat);
                    return category ? category.name : cat;
                  })
                  .join(', ')} Vendors Near You`
              : 'Wedding Vendors Near You'}
          </SectionTitle>

          <FiltersRow>
            <FilterGroup>
              <FilterButton
                active={activeFilter === 'all'}
                onClick={() => setActiveFilter('all')}
              >
                All Vendors
              </FilterButton>
              <FilterButton
                active={activeFilter === 'featured'}
                onClick={() => setActiveFilter('featured')}
              >
                Featured
              </FilterButton>
            </FilterGroup>

            <FilterGroup>
              <SortSelect
                value={sortBy}
                onChange={e => setSortBy(e.target.value)}
              >
                <option value="distance">Sort by Distance</option>
                <option value="rating">Sort by Rating</option>
                <option value="name">Sort by Name</option>
              </SortSelect>
            </FilterGroup>
          </FiltersRow>

          {filteredVendors.length > 0 ? (
            <VendorsGrid>
              {filteredVendors.map(vendor => (
                <VendorCard
                  key={vendor.id}
                  onClick={() => handleVendorClick(vendor)}
                >
                  <VendorImage src={vendor.image} alt={vendor.name} />
                  <VendorContent>
                    <VendorHeader>
                      <VendorInfo>
                        <VendorName>{vendor.name}</VendorName>
                        <VendorLocation>
                          <FaMapMarkerAlt />
                          {vendor.distance}mi  {vendor.city}, {vendor.state}
                        </VendorLocation>
                        <VendorRating>
                          <FaStar />
                          {vendor.rating} ({vendor.reviewCount} reviews)
                        </VendorRating>
                      </VendorInfo>
                      {vendor.featured && <VendorBadge>Featured</VendorBadge>}
                    </VendorHeader>

                    <VendorDescription>{vendor.description}</VendorDescription>

                    <SpecialtiesGrid>
                      {vendor.specialties
                        .slice(0, 4)
                        .map((specialty, index) => (
                          <SpecialtyTag key={index}>{specialty}</SpecialtyTag>
                        ))}
                    </SpecialtiesGrid>

                    <VendorFooter>
                      <ContactInfo>
                        <ContactItem>
                          <FaPhoneAlt />
                          Call
                        </ContactItem>
                        <ContactItem>
                          <FaEnvelope />
                          Email
                        </ContactItem>
                      </ContactInfo>
                      <ViewButton>View Details</ViewButton>
                    </VendorFooter>
                  </VendorContent>
                </VendorCard>
              ))}
            </VendorsGrid>
          ) : (
            <EmptyState>
              <h3>No vendors found</h3>
              <p>
                Try searching for a different location or adjusting your
                filters.
              </p>
            </EmptyState>
          )}
        </VendorsSection>
      </Container>
    </PageContainer>
  );
};

export default WeddingHome;
